import { useState, useEffect } from "react";
import "./InvestorsList.css";

const InvestorsList = () => {
      const [investors, setInvestors] = useState([]);
      const [loading, setLoading] = useState(true);
      const [error, setError] = useState(null);

      const API_BASE_URL =
            process.env.NODE_ENV === "production"
                  ? "https://finlete-be.vercel.app//api"
                  : "http://localhost:30001/api";
      const DEAL_ID = "3701";

      useEffect(() => {
            fetchInvestors();
      }, []);

      const fetchInvestors = async (retryCount = 0) => {
            try {
                  setLoading(true);
                  console.log(
                        `Fetching investors (attempt ${retryCount + 1})...`
                  );

                  const response = await fetch(
                        `${API_BASE_URL}/deals/${DEAL_ID}/investors`,
                        {
                              method: "GET",
                              mode: "cors",
                              cache: "no-cache",
                        }
                  );

                  if (!response.ok) {
                        throw new Error(
                              `HTTP error! status: ${response.status}`
                        );
                  }

                  const data = await response.json();
                  setInvestors(data.items || []);
                  setError(null);
                  console.log("Investors fetched successfully");
            } catch (err) {
                  console.error("Error fetching investors:", err);

                  // Retry up to 3 times with exponential backoff
                  if (retryCount < 3) {
                        const delay = Math.pow(2, retryCount) * 1000; // 1s, 2s, 4s
                        console.log(`Retrying in ${delay}ms...`);
                        setTimeout(() => fetchInvestors(retryCount + 1), delay);
                        return;
                  }

                  setError(err.message);
            } finally {
                  if (retryCount === 0) {
                        setLoading(false);
                  }
            }
      };

      const formatCurrency = (amount) => {
            return new Intl.NumberFormat("en-US", {
                  style: "currency",
                  currency: "USD",
            }).format(amount);
      };

      const formatDate = (dateString) => {
            return new Date(dateString).toLocaleDateString("en-US", {
                  year: "numeric",
                  month: "short",
                  day: "numeric",
            });
      };

      const getStateColor = (state) => {
            switch (state) {
                  case "accepted":
                        return "#22c55e";
                  case "inactive":
                        return "#6b7280";
                  default:
                        return "#6b7280";
            }
      };

      const getFundingStateColor = (fundingState) => {
            switch (fundingState) {
                  case "funded":
                        return "#22c55e";
                  case "unfunded":
                        return "#ef4444";
                  default:
                        return "#6b7280";
            }
      };

      if (loading) {
            return (
                  <div className='investors-container'>
                        <div className='loading'>
                              <div className='spinner'></div>
                              <p>Loading investors...</p>
                        </div>
                  </div>
            );
      }

      if (error) {
            return (
                  <div className='investors-container'>
                        <div className='error'>
                              <h3>‚ùå Error loading investors</h3>
                              <p>{error}</p>
                              <button
                                    onClick={fetchInvestors}
                                    className='retry-btn'>
                                    üîÑ Retry
                              </button>
                        </div>
                  </div>
            );
      }

      return (
            <div className='investors-container'>
                  <header className='investors-header'>
                        <h1>üë• Deal Investors</h1>
                        <p>
                              Deal ID: {DEAL_ID} | API: {API_BASE_URL}
                        </p>
                        <button
                              onClick={fetchInvestors}
                              className='refresh-btn'>
                              üîÑ Refresh
                        </button>
                  </header>

                  <div className='investors-stats'>
                        <div className='stat'>
                              <span className='stat-number'>
                                    {investors.length}
                              </span>
                              <span className='stat-label'>
                                    Total Investors
                              </span>
                        </div>
                        <div className='stat'>
                              <span className='stat-number'>
                                    {
                                          investors.filter(
                                                (inv) =>
                                                      inv.state === "accepted"
                                          ).length
                                    }
                              </span>
                              <span className='stat-label'>Accepted</span>
                        </div>
                        <div className='stat'>
                              <span className='stat-number'>
                                    {
                                          investors.filter(
                                                (inv) =>
                                                      inv.funding_state ===
                                                      "funded"
                                          ).length
                                    }
                              </span>
                              <span className='stat-label'>Funded</span>
                        </div>
                        <div className='stat'>
                              <span className='stat-number'>
                                    {formatCurrency(
                                          investors.reduce(
                                                (sum, inv) =>
                                                      sum +
                                                      (inv.investment_value ||
                                                            0),
                                                0
                                          )
                                    )}
                              </span>
                              <span className='stat-label'>
                                    Total Investment
                              </span>
                        </div>
                  </div>

                  <div className='investors-grid'>
                        {investors.map((investor) => (
                              <div
                                    key={investor.id}
                                    className='investor-card'>
                                    <div className='investor-header'>
                                          <div className='investor-title'>
                                                <h3>
                                                      {investor.name ||
                                                            "Unnamed Investor"}
                                                </h3>
                                                <div className='status-badges'>
                                                      <span
                                                            className='state-badge'
                                                            style={{
                                                                  backgroundColor:
                                                                        getStateColor(
                                                                              investor.state
                                                                        ),
                                                            }}>
                                                            {investor.state.toUpperCase()}
                                                      </span>
